# 百度ocr
def get_access_token():
    url = 'https://aip.baidubce.com/oauth/2.0/token'
    data = {
        'grant_type': 'client_credentials',  # 固定值
        'client_id': 'PHrloRAI7PdSbzyxcf1eIlZW',  # 在开放平台注册后所建应用的API Key
        'client_secret': 'yRLlHXYcLbGvAf5qiHUQOYzj7UlXrTWg'  # 所建应用的Secret Key
    }
    res = requests.post(url, data=data)
    res = res.json()
    # print(res)
    access_token = res['access_token']
    # print(access_token)
    return access_token

def general_word(data):
    #通用文字识别接口url
    general_word_url = "https://aip.baidubce.com/rest/2.0/ocr/v1/accurate_basic"
    if ";base64," in data:
        b64_data2 = data.split(';base64,')[1]
    else:
        b64_data2 = data
    # 二进制方式打开图片文件
    img = b64_data2
    # print(img)
    params = {"image":img,
              "language_type":"CHN_ENG"}
    access_token = get_access_token()
    request_url = general_word_url + "?access_token=" + access_token
    # print(request_url)
    headers = {'content-type': 'application/x-www-form-urlencoded'}
    response = requests.post(request_url, data=params, headers=headers)
    # print(response)
    res = response.json()
    # print(res)
    if "words_result" in res:
        return res["words_result"][0]["words"]
    else:
        return 0
general_word = general_word
